import { useState } from 'react';
import './App.css';

function App() {

  const [logado, setLogado] = useState(false);
  const [nome, setNome] = useState("");
  const [sala, setSala] = useState("");

  const conectarSala = () => {
    alert("Acessou a sala " + sala + ", com o usu치rio " + nome + "!");
    setLogado(true);
  }

  return (
    <div className="App">
      <h1>Chat</h1>
      {!logado ? "Necess치rio realizar o login" : "Logado"}
      <div>
        <label>Nome: </label>
        <input type="text" placeholder="Nome" name="nome" value={nome} onChange={(text) => { setNome(text.target.value) }} />
      </div>
      <div>
        <label>Sala: </label>
        <input type="text" placeholder="Sala" value={sala} onChange={(text) => { setSala(text.target.value) }} />
        <select name="sala" value={sala}>
          <option value="">Selecione</option>
          <option value="1">Node.js</option>
          <option value="2">React</option>
          <option value="3">React Native</option>
          <option value="4">Next</option>
        </select>
      </div>
      <div>
        <button onClick={conectarSala}>Conectar</button>
      </div>
    </div>
  );
}

export default App;
