import { useState } from 'react';
import './App.css';

function App() {

  const [logado, setLogado] = useState(false);
  const [nome, setNome] = useState("");
  const [sala, setSala] = useState("");

  return (
    <div className="App">
      <h1>Chat</h1>
      {!logado ? "Necessário realizar o login" : "Logado"}
      <div>
        <label>Nome: </label>
        <input type="text" placeholder="Nome" value={nome} onChange={(text) => { setNome(text.target.value) }} />
      </div>
      <div>
        <label>Sala: </label>
        <input type="text" placeholder="Sala" value={sala} onChange={(text) => { setSala(text.target.value) }} />
      </div>
      <div>
        <button>Conectar</button>
      </div>
    </div>
  );
}

export default App;
